int is_move_okay (int y, int x) {
    int testch;
    testch = mvinch(y, x);
    return ((testch & A_CHARTEXT) == EMPTY);
}

int is_move_bushes(int y, int x) {
    int testch;
    testch = mvinch(y, x);
    return ((testch & A_CHARTEXT) == BUSHES);
}

